import { createGlobalStyle } from "styled-components";

export const GlobalStyle = createGlobalStyle`
    *{
        margin: 0;
        padding: 0;
    }

    body {
        background-color: #fff;
    }

    input,select{
        outline: none;
    }

    :root {
       --color-primary:
       --color-gradient:
    }
`
